function skb_bookdetails_shortcode()
{


    $post_titel = get_the_title();
    $rating = get_post_meta(get_the_ID(), 'book_rating', true);
    $post_id = get_the_ID();
    $categories = get_the_category(); // Returns an array of categories
    if ($categories) {
        foreach ($categories as $category) {
            $category_link = get_category_link($category);
            $catego = $category->name;
            break;
        }
    } else{
        $catego = '';
    }
    ?>

    <div class="bookdetailsright">
        <div class="container">
            <div class="row">
                <div class="col-md-12">
                    <div class="category-name">
                        <p class="category">
                            <?php echo $catego ?>
